CW.ellipsoid <- function(SA, CWCL){

  omega <- function(x){

    res <- rep(NA, length(x))

    res[x<1] <- 1+asin(sqrt(1-x[x<1]^2))/(x[x<1]*sqrt(1-x[x<1]^2))
    res[x>1] <- 1+log(x[x>1]+sqrt(x[x>1]^2-1))/(x[x>1]*sqrt(x[x>1]^2-1))
    res
  }

  CW <- 2*sqrt(SA/(2*pi*omega(CWCL)))

  return(CW)
}
